mapper.insert:
INSERT INTO TBL_BOARD(
	bno
	, type
	, title
	, content
	, writer
	, hits
	, like_cnt
	, mem_cnt
	, tot_mem_cnt
	, status
	, reg_date
	, update_date
	) 
VALUES(
	#{bno}
	, #{type}
	, #{title}
	, #{content}
	, #{writer}
	, #{hits}
	, #{likeCnt}
	, #{memCnt}
	, #{totMemCnt}
	, #{status}
	, #{regDate}
	, #{updateDate}
	)

mapper.select:
SELECT bno ,bType ,title ,content ,writer ,hits ,like_cnt likeCnt ,mem_cnt memCnt ,tot_mem_cnt totMemCnt ,status ,thumnail ,reg_date ,update_date FROM TBL_BOARD WHERE
and bType = #{bType}
rownum <= #{pageNum} * #{amount} ) where rn > (#{pageNum} - 1) * #{amount}

mapper.update:
UPDATE TBL_BOARD 
   SET bno = #{bno}
	,type = #{type}
	,title = #{title}
	,content = #{content}
	,writer = #{writer}
	,hits = #{hits}
	,like_cnt = #{likeCnt}
	,mem_cnt = #{memCnt}
	,tot_mem_cnt = #{totMemCnt}
	,status = #{status}
	,thumnail = #{thumnail}
	,reg_date = #{regDate}
	,update_date = #{updateDate}
 WHERE bno = #{bno}
